mapper.select:
SELECT HELPERNO "helperNo", HELPERNAME "helperName", HELPERPROFILE "helperProfile", HELPERWORKAREA "helperWorkArea", HELPERSTARTTIME "helperStartTime", HELPERENDTIME "helperEndTime" FROM HELPER
HELPERWORKAREA LIKE #{helperWorkArea}
AND HELPERSTARTTIME <= #{helperStartTime}
AND HELPERENDTIME >= #{helperEndTime}
AND HELPERNO NOT IN (SELECT HELPERNO "helperNo" FROM BLACKLIST WHERE FAMILYNO = #{familyNo})
