mapper.select:
SELECT REALSTARTTIME "realStartTime"
FROM SERVICEHISTORY
WHERE SERVICENO = #{value}

SELECT REALENDTIME "realEndTime"
FROM SERVICEHISTORY 
WHERE SERVICENO = #{value}

mapper.update:
UPDATE SERVICEHISTORY
SET REALENDTIME = SYSDATE
WHERE SERVICENO = #{serviceNo}

UPDATE SERVICEAPPLY
SET SERVICESTATUS = 'S04'
WHERE SERVICENO = #{serviceNo}
